import React, { createContext, useState } from 'react';
import { useToasts } from 'react-toast-notifications';
// import cloneDeep from 'lodash.cloneDeep' <-- use if your objects get complex

export const LibrariesContext = createContext({
  getLibrries: () => [],
  addLibrary: () => {},
  updateLibrary: () => {},
  deleteLibrary: () => {},
  loaded: false,
  loading: false,
  error: null,
  libraries: [],
});

export const LibrariesProvider = (props) => {
  const [libraries, setLibraries] = useState([]);
  // const [loading, setLoading] = useState(false);
  // const [loaded, setLoaded] = useState(false);
  // const [error, setError] = useState(null);
  // const [search, setSearch] = useState("");
  const { addToast } = useToasts();

  const getLibraries = (libraries) => {
    setLibraries(libraries);
  };

  const addLibrary = (library) => {
    setLibraries([...libraries, library]);
  };

  const updateLibrary = (id, updates) => {
    let newPerson = null;

    // Get index
    const index = libraries.findIndex((library) => library._id === id);

    // Get actual person
    const oldLibrary = libraries[index];

    // Merge with updates
    newLibrary = {
      // legit use of 'var', so can be seen in catch block
      ...oldLibrary,
      ...updates, // order here is important for the override!!
    };
    // recreate the libs array
    const updatedLibrary = [
      ...libraries.slice(0, index),
      newLibrary,
      ...libraries.slice(index + 1),
    ];
    setLibraries(updatedLibrary);
    addToast(`Updated ${newLibrary.name}`, {
      appearance: 'success',
    });
  };

  const deleteLibrary = async (id) => {
    let deletedLibrary = null;
    try {
      const response = await fetch(`/api/v1/people/${id}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          // 'Content-Type': 'application/x-www-form-urlencoded',
        },
      });
      if (response.status !== 204) {
        throw response;
      }
      // Get index
      const index = people.findIndex((person) => person._id === id);
      deletedPerson = people[index];
      // recreate the people array without that person
      const updatedPeople = [
        ...people.slice(0, index),
        ...people.slice(index + 1),
      ];
      setPeople(updatedPeople);
      addToast(`Deleted ${deletedPerson.firstName} ${deletedPerson.lastName}`, {
        appearance: 'success',
      });
    } catch (err) {
      console.log(err);
      addToast(
        `Error: Failed to update ${deletedPerson.firstName} ${deletedPerson.lastName}`,
        {
          appearance: 'error',
        }
      );
    }
  };

  return (
    <PeopleContext.Provider
      value={{
        people,
        loading,
        error,
        fetchPeople,
        addPerson,
        updatePerson,
        deletePerson,
      }}
    >
      {props.children}
    </PeopleContext.Provider>
  );
};
